import { SearchIcon, ArrowForwardIcon } from "@chakra-ui/icons";
import React, { useState, useEffect } from "react";
import UserComponent from "./SearchCard";
import axios from "axios";

import {
  Box,
  Flex,
  Heading,
  Input,
  InputGroup,
  InputLeftElement,
  Link,
  Stack,
  Popover,
  PopoverTrigger,
  PopoverContent,
  PopoverHeader,
  PopoverBody,
  PopoverArrow,
  PopoverCloseButton,
} from "@chakra-ui/react";
import RsidebarCard from "./RsidebarCard";

const Rsidebar = () => {
  const initialFocusRef = React.useRef();
  let [searchQuery, setSearchQuery] = useState("");
  let [dataJSON, setData] = useState([]);
  let [queryData, setqueryData] = useState([]);

  useEffect(() => {
    const json_URL = `${process.env.REACT_APP_API_KEY}/peopleData`;

    const getInitialState = async () => {
      let res = await axios.get(json_URL);
      res = res.data;
      setData(res);
    };

    getInitialState();
  }, []);

  const hashData = [
    { hastag: "#ipl2023" ,img1: 'https://img.cricketworld.com/images/f-122898/fkqtiglxkaaw0xn.jpg',img2: 'https://static.toiimg.com/thumb/msid-98014197,width-1280,resizemode-4/98014197.jpg', 
    img3:'https://images.mid-day.com/images/images/2023/mar/wdwdefdfgipl_d.jpg',img4:'https://exchange4media.gumlet.com/news-photo/125255-starmain.jpg'},
    { hastag: "#amritpalsingh" ,img1: 'https://www.hindustantimes.com/ht-img/img/2023/04/23/550x309/PTI04-23-2023-000048B-0_1682241681415_1682241705047.jpg',
    img2: 'data:image/jpeg;base64,', 
    img3:'https://akm-img-a-in.tosshub.com/indiatoday/images/story/202303/amritpal_singh_sandhu-12012023-ban_1_0-sixteen_nine.jpg?VersionId=JFdmybNB4n9Geq9oMvETPCts8Bd3XOaz',
    img4:'https://www.hindustantimes.com/ht-img/img/2023/04/23/1600x900/amritpal_1682234226089_1682234226278.webp'},
    { hastag: "#nrupursharma" ,img1: 'https://pbs.twimg.com/profile_images/1495804777732603905/3wo-lYCK_400x400.jpg',
    img2: 'https://ichef.bbci.co.uk/news/976/cpsprodpb/45C4/production/_125306871_gettyimages-461877666-594x594.jpg', img3:'https://ichef.bbci.co.uk/news/976/cpsprodpb/FBD0/production/_125346446_gettyimages-1241141311-594x594.jpg',
    img4:'https://m.economictimes.com/thumb/msid-92053640,width-657,height-900,resizemode-4,imgsize-72758/nupur-sharma.jpg'},
    { hastag: "#rcbvsmi" ,img1: 'https://www.icccricketschedule.com/wp-content/uploads/2023/04/RCB-vs-MI.jpg',img2: 'https://hellotricity.in/wp-content/uploads/2020/09/rcb-vsmi.jpg',
     img3:'https://c.ndtvimg.com/2023-04/8vv50p9_virat-kohli-bcci_625x300_02_April_23.jpg?im=FaceCrop,algorithm=dnn,width=806,height=605',img4:'data:image/jpeg;base64,'},
  ];

  const filterSearch = () => {
    let filtered = dataJSON.filter((val) => {
      if (searchQuery === "") {
        return val;
      } else if (val.name.toLowerCase().includes(searchQuery.toLowerCase())) {
        return val;
      }
    });

    setqueryData(filtered);
  };

  return (
    <Flex
      direction={"column"}
      m="auto"
      h="700px"
      position="sticky"
      top="0"
      pt="3%"
    >
      <Box>
        <Stack spacing={4} p="10px">
          <InputGroup>
            <InputLeftElement
              pointerEvents="none"
              children={<SearchIcon color="gray.500" />}
            />
            <Popover
              isLazy
              initialFocusRef={initialFocusRef}
              placement="left"
              closeOnBlur={false}
              trigger="hover"
            >
              <PopoverTrigger>
                <Input
                  onChange={(e) => {
                    setSearchQuery(e.target.value);
                    filterSearch();
                  }}
                  value={searchQuery}
                  type="text"
                  placeholder="Search"
                  borderRadius={"20px"}
                  bg="#e6e6e3"
                  pl="50px"
                />
              </PopoverTrigger>
              <PopoverContent>
                <PopoverHeader pt={4} fontWeight="bold" border="0">
                  People to Follow
                </PopoverHeader>
                <PopoverArrow />
                <PopoverCloseButton />
                <PopoverBody>
                  {queryData.map((ele, idx) => {
                    return <UserComponent ele={ele} />;
                  })}
                </PopoverBody>
              </PopoverContent>
            </Popover>
          </InputGroup>
        </Stack>
      </Box>
      <Box>
        <Flex justifyContent={"space-between"} padding="0 20px">
          <Heading size={"30px"} fontWeight={"500"}>
            {" "}
            Trending Hashtags
          </Heading>
          <ArrowForwardIcon boxSize={5} />
        </Flex>
      </Box>
      <Flex
        direction={"column"}
        gap="20px"
        mt={"30px"}
        className="allCardContainer"
      >
        {hashData.map((ele) => {
          return <RsidebarCard ele={ele} />;
        })}
      </Flex>
      <Box mt="1rem">
        <Flex
          justifyContent={"space-around"}
          fontSize="12px"
          m="auto"
          pt="1rem"
        >
          <Link href="http://www.google.com" color={"rgb(136,136,136)"}>
            About
          </Link>
          <Link to="http://www.google.com" color={"rgb(136,136,136)"}>
            Press
          </Link>
          <Link to="http://www.google.com" color={"rgb(136,136,136)"}>
            Contact
          </Link>
          <Link to="http://www.google.com" color={"rgb(136,136,136)"}>
            Resource Center
          </Link>
          <Link to="http://www.google.com" color={"rgb(136,136,136)"}>
            FAQs
          </Link>
        </Flex>
      </Box>
    </Flex>
  );
};

export default Rsidebar;